frappe.ui.form.on('KTA machine capability study', {
    validate(frm) {
        const ara_olcum_sayisi = 5;
        let olcum_toplami = 0;
        let ara_olcum_toplami = 0;
        let veri_sayisi = 0;
        let max_veri = null;
        let min_veri = null;
        let max_ara_olcum = null;
        let min_ara_olcum = null;
        let outOfSpecCount = 0;
        
        if (frm.doc.veri_tablosu && frm.doc.veri_tablosu.length > 0) {
            frm.doc.veri_tablosu.forEach(function(d) {
                const veri = flt(d.veri);
                
                if (!isNaN(veri) && veri !== 0) {
                    if (frm.doc.upper_limit !== undefined && frm.doc.lower_limit !== undefined) {
                        if (veri < frm.doc.lower_limit || veri > frm.doc.upper_limit) {
                            outOfSpecCount++;
                            frappe.model.set_value(d.doctype, d.name, 'out_of_spec', true);
                        } else {
                            frappe.model.set_value(d.doctype, d.name, 'out_of_spec', false);
                            if (min_veri === null) {
                                min_veri = veri;
                            } else {
                                if (veri < min_veri) {
                                    min_veri = veri;
                                }
                            }
                            if (max_veri === null) {
                                max_veri = veri;
                            } else {
                                if (veri > max_veri) {
                                    max_veri = veri;
                                }
                            }
                        }
                    }
                    olcum_toplami += veri;
                    ara_olcum_toplami += veri;
                    veri_sayisi += 1;
                }

                if (d.idx % ara_olcum_sayisi === 0 && ara_olcum_toplami !== 0) {
                    const ara_olcum = flt(ara_olcum_toplami / ara_olcum_sayisi);
                    
                    frappe.model.set_value(d.doctype, d.name, 'ara_olcum_ortalamasi', ara_olcum);
                    if (min_ara_olcum === null) {
                        min_ara_olcum = ara_olcum;
                    } else {
                        if (ara_olcum < min_ara_olcum) {
                            min_ara_olcum = ara_olcum;
                        }
                    }
                    if (max_ara_olcum === null) {
                        max_ara_olcum = ara_olcum;
                    } else {
                        if (ara_olcum > max_ara_olcum) {
                            max_ara_olcum = ara_olcum;
                        }
                    }
                    ara_olcum_toplami = 0;
                }
            });
        }
        
        // Calculate mean
        let mean_value = 0;
        if (veri_sayisi > 0) {
            mean_value = olcum_toplami / veri_sayisi;
        }
        
        // Update fields with if/else statements
        frm.set_value('mean_value', mean_value);
        frm.set_value('standard_deviation', 0);
        frm.set_value('cm_value', 0);
        frm.set_value('cmk_value', 0);
        frm.set_value('cpk_value', 0);
        
        if (max_ara_olcum === null) {
            frm.set_value('tepe_degerixq', 0);
        } else {
            frm.set_value('tepe_degerixq', max_ara_olcum);
        }
        
        if (min_ara_olcum === null) {
            frm.set_value('dip_degerixq', 0);
        } else {
            frm.set_value('dip_degerixq', min_ara_olcum);
        }
        
        if (max_veri === null) {
            frm.set_value('tepe_deger', 0);
        } else {
            frm.set_value('tepe_deger', max_veri);
        }
        
        if (min_veri === null) {
            frm.set_value('dip_deger', 0);
        } else {
            frm.set_value('dip_deger', min_veri);
        }
    }
});

