frappe.ui.form.on('Work Order', {
    refresh: function(frm) {
        if (frm.doc.custom_kesim_formu_link) {
            frm.add_custom_button('Kesim Formunu Aç', function () {
                window.open(frm.doc.custom_kesim_formu_link, '_blank');
            }, 'View');  // View altına geldi
        }
