frappe.ui.form.on('KTA machine capability study', {
    validate: function(frm) {
        // Constants
        var MEASUREMENTS_PER_GROUP = 5;
        var ROUNDING_DECIMALS = 6;
        var STANDARD_DIVISOR = 7.67; // 6 + 1.67
        var SQRT5 = Math.sqrt(5);
        var CONFIDENCE_FACTOR = 2.58; // 99% confidence
        var D2_CORRECTION = 0.94; // For subgroup size 5
        
        // Labels
        var CAPABLE_LABEL = "capable";
        var INCAPABLE_LABEL = "incapable";
        
        // Get form values
        var upperLimit = frm.doc.upper_limit;
        var lowerLimit = frm.doc.lower_limit;
        var nominalValue = frm.doc.nominal_value;
        var targetCmk = frm.doc.soll_cmk;

        // Validate inputs
        if (upperLimit <= lowerLimit) {
            frappe.msgprint(__('Upper limit must be greater than lower limit'));
            return;
        }

        // Initialize variables
        var toleranceWidth = upperLimit - lowerLimit;
        var targetStandard = toleranceWidth / STANDARD_DIVISOR;
        
        var allMeasurements = [];
        var subgroupMeasurements = [];
        var subgroupAverages = [];
        var subgroupStdDevs = [];
        
        var sumAllMeasurements = 0;
        var sumSubgroupMeasurements = 0;
        var sumSubgroupStdDevs = 0;
        
        var maxMeasurement = null;
        var minMeasurement = null;
        var maxSubgroupAvg = null;
        var minSubgroupAvg = null;

        // Process measurement data
        if (frm.doc.veri_tablosu && frm.doc.veri_tablosu.length > 0) {
            frm.doc.veri_tablosu.forEach(function(row) {
                var measurement = flt(row.veri);
                
                if (isNaN(measurement) || measurement === 0) {
                    return; // Skip invalid measurements
                }

                // Check specification limits
                var outOfSpec = false;
                if (measurement < lowerLimit || measurement > upperLimit) {
                    outOfSpec = true;
                }
                frappe.model.set_value(row.doctype, row.name, 'out_of_spec', outOfSpec);
                
                if (!outOfSpec) {
                    // Track all in-spec measurements
                    allMeasurements.push(measurement);
                    sumAllMeasurements += measurement;
                    
                    // Track min/max measurements
                    if (minMeasurement === null) {
                        minMeasurement = measurement;
                    } else if (measurement < minMeasurement) {
                        minMeasurement = measurement;
                    }
                    
                    if (maxMeasurement === null) {
                        maxMeasurement = measurement;
                    } else if (measurement > maxMeasurement) {
                        maxMeasurement = measurement;
                    }
                    
                    // Build subgroups
                    subgroupMeasurements.push(measurement);
                    sumSubgroupMeasurements += measurement;
                }

                // Process complete subgroups
                if (row.idx % MEASUREMENTS_PER_GROUP === 0 && subgroupMeasurements.length > 0) {
                    var subgroupAvg = sumSubgroupMeasurements / subgroupMeasurements.length;
                    subgroupAverages.push(subgroupAvg);
                    
                    // Track min/max subgroup averages
                    if (minSubgroupAvg === null) {
                        minSubgroupAvg = subgroupAvg;
                    } else if (subgroupAvg < minSubgroupAvg) {
                        minSubgroupAvg = subgroupAvg;
                    }
                    
                    if (maxSubgroupAvg === null) {
                        maxSubgroupAvg = subgroupAvg;
                    } else if (subgroupAvg > maxSubgroupAvg) {
                        maxSubgroupAvg = subgroupAvg;
                    }
                    
                    // Calculate subgroup standard deviation
                    var sumSquaredDeviations = 0;
                    for (var i = 0; i < subgroupMeasurements.length; i++) {
                        var deviation = subgroupMeasurements[i] - subgroupAvg;
                        sumSquaredDeviations += deviation * deviation;
                    }
                    
                    var subgroupStdDev = 0;
                    if (subgroupMeasurements.length > 1) {
                        subgroupStdDev = Math.sqrt(sumSquaredDeviations / (subgroupMeasurements.length - 1));
                    }
                    
                    subgroupStdDevs.push(subgroupStdDev);
                    sumSubgroupStdDevs += subgroupStdDev;
                    
                    // Save subgroup stats
                    frappe.model.set_value(row.doctype, row.name, 'ara_olcum_ortalamasi', 
                        flt(subgroupAvg).toFixed(ROUNDING_DECIMALS));
                    frappe.model.set_value(row.doctype, row.name, 'ara_olcum_std_sapmasi', 
                        flt(subgroupStdDev).toFixed(ROUNDING_DECIMALS));
                    
                    // Reset subgroup
                    subgroupMeasurements = [];
                    sumSubgroupMeasurements = 0;
                }
            });
        }

        // Set min/max values
        if (maxMeasurement === null) {
            frm.set_value('tepe_deger', 0);
        } else {
            frm.set_value('tepe_deger', maxMeasurement);
        }
        
        if (minMeasurement === null) {
            frm.set_value('dip_deger', 0);
        } else {
            frm.set_value('dip_deger', minMeasurement);
        }
        
        if (maxSubgroupAvg === null) {
            frm.set_value('tepe_degerixq', 0);
        } else {
            frm.set_value('tepe_degerixq', maxSubgroupAvg);
        }
        
        if (minSubgroupAvg === null) {
            frm.set_value('dip_degerixq', 0);
        } else {
            frm.set_value('dip_degerixq', minSubgroupAvg);
        }

        // Calculate mean value
        var meanValue = 0;
        if (allMeasurements.length > 0) {
            meanValue = sumAllMeasurements / allMeasurements.length;
        }
            
        // Calculate mean subgroup standard deviation
        var meanSubgroupStdDev = targetStandard;
        if (subgroupStdDevs.length > 0) {
            meanSubgroupStdDev = sumSubgroupStdDevs / subgroupStdDevs.length;
        }
            
        // Calculate standard deviation of all measurements
        var sumSquaredDeviationsAll = 0;
        for (var i = 0; i < allMeasurements.length; i++) {
            var deviation = allMeasurements[i] - meanValue;
            sumSquaredDeviationsAll += deviation * deviation;
        }
        
        var stdDevAll = 0;
        if (allMeasurements.length > 1) {
            stdDevAll = Math.sqrt(sumSquaredDeviationsAll / (allMeasurements.length - 1));
        }

        // Calculate capability indices
        var cm = toleranceWidth / (6 * meanSubgroupStdDev);
        var cmkUpper = (upperLimit - meanValue) / (3 * meanSubgroupStdDev);
        var cmkLower = (meanValue - lowerLimit) / (3 * meanSubgroupStdDev);
        
        var cmk = cmkUpper;
        if (cmkLower < cmkUpper) {
            cmk = cmkLower;
        }
        
        // Additional capability indices
        var cmA = maxMeasurement / (6 * stdDevAll);
        var cmkUn = (minMeasurement - lowerLimit) / (3 * stdDevAll);
        var cmkOb = (upperLimit - maxMeasurement) / (3 * stdDevAll);

        // Calculate control limits
        var upperControlLimit = meanValue + (CONFIDENCE_FACTOR / SQRT5) * (meanSubgroupStdDev / D2_CORRECTION);
        var lowerControlLimit = meanValue - (CONFIDENCE_FACTOR / SQRT5) * (meanSubgroupStdDev / D2_CORRECTION);

        // Set calculated values
        frm.set_value('mean_value', flt(meanValue).toFixed(ROUNDING_DECIMALS));
        frm.set_value('standard_deviation', flt(meanSubgroupStdDev).toFixed(ROUNDING_DECIMALS));
        frm.set_value('cm_value', flt(cm).toFixed(ROUNDING_DECIMALS));
        frm.set_value('cmk_value', flt(cmk).toFixed(ROUNDING_DECIMALS));
        
        // Set capability labels
        if (cm > targetCmk) {
            frm.set_value('cm_capable', CAPABLE_LABEL);
        } else {
            frm.set_value('cm_capable', INCAPABLE_LABEL);
        }
        
        if (cmk > targetCmk) {
            frm.set_value('cmk_capable', CAPABLE_LABEL);
        } else {
            frm.set_value('cmk_capable', INCAPABLE_LABEL);
        }
        
        // Set control limits
        frm.set_value('cmk_performance_oeg', flt(upperControlLimit).toFixed(ROUNDING_DECIMALS));
        frm.set_value('cmk_performance_ueg', flt(lowerControlLimit).toFixed(ROUNDING_DECIMALS));
        
        // Set deviation from nominal
        frm.set_value('xq_deviation', flt(Math.abs(nominalValue - meanValue)).toFixed(ROUNDING_DECIMALS));
        
        // Additional capability check
        if (targetCmk < cmkUn || targetCmk < cmkOb) {
            frm.set_value('cm_capable', CAPABLE_LABEL);
        }
    }
});

