script:
frappe.ui.form.on('Sales Order', {
//	refresh(frm) {
		// your code here
//	}
	make_manufacture_request: function(frm){
	    if((frm.doc.docstatus === 1 && doc.status !== "Closed" && doc.status !== "On Hold")){
	        // Call the server-side method to create the Material Request
            frappe.call({
                method: 'erpnextkta.api.make_manufacture_request',
                args: {
                    source_name: frm.doc.name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.set_route('Form', 'Material Request', r.message);
                    }
                }
	        });
	    }
	}
});

frappe.ui.form.on('Quality Feedback', {
	validate(frm) {
        // calculate points for parameters on the feedback
        degerlendirme_toplam = 0;
        $.each(frm.doc.parameters,  function(i,  d) {
            // calculate total
            degerlendirme_toplam += flt(d.rating);
        });
        frm.doc.degerlendirme_toplami = degerlendirme_toplam;
	}
});

frappe.ui.form.on('Customer', {
	setup(frm) {
		frm.set_query("customer_income_account", "accounts", function (doc, cdt, cdn) {
			let d = locals[cdt][cdn];
			return {
				filters: {
					account_type: "Income Account",
					root_type: "Income",
					company: d.company,
					is_group: 0,
				},
			};
		});
	}
});

frappe.ui.form.on('Sales Invoice', {
    onload: function(frm) {
        // Populate income account for all items when the form is loaded
        frm.doc.items.forEach(function(item) {
            if (item.item_code && frm.doc.company && frm.doc.customer) {
                fetch_customer_income_account(frm, item);
            }
        });
    },
    refresh: function(frm) {
        // Populate income account for all items when the form is refreshed
        frm.doc.items.forEach(function(item) {
            if (item.item_code && frm.doc.company && frm.doc.customer) {
                fetch_customer_income_account(frm, item);
            }
        });
    }
});

frappe.ui.form.on('Sales Invoice Item', {
    item_code: function(frm, cdt, cdn) {
        let item = frappe.get_doc(cdt, cdn);
        if (item.item_code && frm.doc.company && frm.doc.customer) {
            frappe.call({
                method: 'erpnextkta.api.get_customer_income_account', // Updated method path
                args: {
                    'customer': frm.doc.customer,
                    'company': frm.doc.company
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.model.set_value(cdt, cdn, 'income_account', r.message);
                    } else {
                        frappe.msgprint(__("No customer income account found for the selected customer and company."));
                    }
                }
            });
        }
    }
});

function fetch_customer_income_account(frm, item) {
    frappe.call({
        method: 'erpnextkta.api.get_customer_income_account', // Updated method path
        args: {
            'customer': frm.doc.customer,
            'company': frm.doc.company
        },
        callback: function(r) {
            if (r.message) {
                frappe.model.set_value(item.doctype, item.name, 'income_account', r.message);
            } else {
                frappe.msgprint(__("No customer income account found for the selected customer and company."));
            }
        }
    });
}

frappe.ui.form.on('Job Card', {
    refresh: function(frm) {
        // Set "Ignore User Permissions" for the field
        frm.set_df_property('employee', 'ignore_user_permissions', 1);
    }
});

frappe.ui.form.on('D1 Takim Uyeleri', {
    refresh: function(frm) {
        // Set "Ignore User Permissions" for the field in the child table
        frm.set_df_property('d1_takim_uyesi', 'ignore_user_permissions', 1);
    }
});

frappe.ui.form.on('Work Order', {
    refresh: function(frm) {
        if (frm.doc.custom_kesim_formu_link) {
            frm.add_custom_button('Kesim Formunu Aç', function () {
                window.open(frm.doc.custom_kesim_formu_link, '_blank');
            }, 'View');  // View altına geldi
        }
    }
});

frappe.ui.form.on('Project', {
    refresh(frm) {
        if (frm.doc.project_name) {
            const folderName = encodeURIComponent(frm.doc.project_name);
            const path = `/app/file?folder=Home/Projeler/${folderName}`;

            frm.add_custom_button(__('Proje Klasörü'), function () {
                window.open(path, '_blank');
            });
        }
    }
});

frappe.ui.form.on("Item Price", {
    item_code: function(frm) {
        if (frm.doc.item_code) {
            frappe.call({
                method: "frappe.client.get_value",
                args: {
                    doctype: "Item",
                    filters: {
                        name: frm.doc.item_code
                    },
                    fieldname: ["custom_ara_malzeme_grubu"]
                },
                callback: function(r) {
                    if (r.message && r.message.custom_ara_malzeme_grubu) {
                        frm.set_value("custom_ara_malzeme_kodu", r.message.custom_ara_malzeme_grubu);
                        console.log("Ara malzeme grubu güncellendi:", r.message.custom_ara_malzeme_grubu);
                    } else {
                        frm.set_value("custom_ara_malzeme_kodu", "");
                        console.warn("Ürün bulundu ama 'custom_ara_malzeme_grubu' boş.");
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Work Order', {
    refresh: function(frm) {
        // Sadece taslak durumda ve kapatılmamış belgelerde çalıştır
        if (
            frm.doc.docstatus === 0 &&
            frm.doc.production_item &&
            !frm.doc.__islocal &&
            frm.doc.status !== "Closed"
        ) {
            set_item_group(frm);
        }
    },
    production_item: function(frm) {
        // Ürün değiştiğinde sadece taslak ve kapatılmamış belgelerde çalıştır
        if (frm.doc.docstatus === 0 && frm.doc.status !== "Closed") {
            set_item_group(frm);
        }
    },
    onload: function(frm) {
        frm.set_df_property('custom_ürün_grubu', 'read_only', 1);
    }
});

// Yardımcı fonksiyon
function set_item_group(frm) {
    if (frm.doc.production_item) {
        frappe.db.get_value("Item", frm.doc.production_item, "item_group")
            .then(r => {
                if (r.message && r.message.item_group) {
                    frm.set_value("custom_ürün_grubu", r.message.item_group);
                }
            });
    }
}

frappe.ui.form.on('KTA Depo Etiketleri', {
// refresh(frm) {
custom_print_label_to_zebra_kta: function(frm){
frappe.call({
            method: "erpnextkta.api.print_to_zebra_kta",
            args:{
                "label": frm.doc.name
            },
            callback: function(r){
                frm.set_value("custom_print_status", r.message);
            }
        });
}
});
