script:
frappe.ui.form.on('Sales Order', {
//	refresh(frm) {
		// your code here
//	}
	make_manufacture_request: function(frm){
	    if((frm.doc.docstatus === 1 && doc.status !== "Closed" && doc.status !== "On Hold")){
	        // Call the server-side method to create the Material Request
            frappe.call({
                method: 'erpnextkta.api.make_manufacture_request',
                args: {
                    source_name: frm.doc.name
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.set_route('Form', 'Material Request', r.message);
                    }
                }
	        });
	    }
	}
});

frappe.ui.form.on('Quality Feedback', {
	validate(frm) {
        // calculate points for parameters on the feedback
        degerlendirme_toplam = 0;
        $.each(frm.doc.parameters,  function(i,  d) {
            // calculate total
            degerlendirme_toplam += flt(d.rating);
        });
        frm.doc.degerlendirme_toplami = degerlendirme_toplam;
	}
});

frappe.ui.form.on('Customer', {
	setup(frm) {
		frm.set_query("customer_income_account", "accounts", function (doc, cdt, cdn) {
			let d = locals[cdt][cdn];
			return {
				filters: {
					account_type: "Income Account",
					root_type: "Income",
					company: d.company,
					is_group: 0,
				},
			};
		});
	}
});

frappe.ui.form.on('Sales Invoice', {
    onload: function(frm) {
        // Populate income account for all items when the form is loaded
        frm.doc.items.forEach(function(item) {
            if (item.item_code && frm.doc.company && frm.doc.customer) {
                fetch_customer_income_account(frm, item);
            }
        });
    },
    refresh: function(frm) {
        // Populate income account for all items when the form is refreshed
        frm.doc.items.forEach(function(item) {
            if (item.item_code && frm.doc.company && frm.doc.customer) {
                fetch_customer_income_account(frm, item);
            }
        });
    }
});

frappe.ui.form.on('Sales Invoice Item', {
    item_code: function(frm, cdt, cdn) {
        let item = frappe.get_doc(cdt, cdn);
        if (item.item_code && frm.doc.company && frm.doc.customer) {
            frappe.call({
                method: 'erpnextkta.api.get_customer_income_account', // Updated method path
                args: {
                    'customer': frm.doc.customer,
                    'company': frm.doc.company
                },
                callback: function(r) {
                    if (r.message) {
                        frappe.model.set_value(cdt, cdn, 'income_account', r.message);
                    } else {
                        frappe.msgprint(__("No customer income account found for the selected customer and company."));
                    }
                }
            });
        }
    }
});

function fetch_customer_income_account(frm, item) {
    frappe.call({
        method: 'erpnextkta.api.get_customer_income_account', // Updated method path
        args: {
            'customer': frm.doc.customer,
            'company': frm.doc.company
        },
        callback: function(r) {
            if (r.message) {
                frappe.model.set_value(item.doctype, item.name, 'income_account', r.message);
            } else {
                frappe.msgprint(__("No customer income account found for the selected customer and company."));
            }
        }
    });
}

frappe.ui.form.on('Job Card', {
    refresh: function(frm) {
        // Set "Ignore User Permissions" for the field
        frm.set_df_property('employee', 'ignore_user_permissions', 1);
    }
});

frappe.ui.form.on('D1 Takim Uyeleri', {
    refresh: function(frm) {
        // Set "Ignore User Permissions" for the field in the child table
        frm.set_df_property('d1_takim_uyesi', 'ignore_user_permissions', 1);
    }
});
